const checkIfObjectOrArray = (value) =>  (
    Object.prototype.toString.call(value) === '[object Array]'
    || Object.prototype.toString.call(value) === '[object Object]'
);

const isString = (value) => (typeof value === 'string' || value instanceof String);

const createTable = (json) => {

    const tableData = Object.keys(json).map((key) => {

        const createNewTableIfObjectOrArray = (value) => {

            if(checkIfObjectOrArray(value)) {
                return `
                    <tr>
                        <td>${key}</td>
                        <td>
                            <span class="expandButton expanded">-</span>
                            ${createTable(value)}
                        </td>
                    </tr>
                `;
            } 

            return `
                <tr>
                    <td>${key}</td>
                    <td class="endValue ${(isString(json[key]) ? 'string' : 'notString')}">${json[key]}</td>
                </tr>
            `;
        }

        return createNewTableIfObjectOrArray(json[key]);
    });

    return `
        <table class="table-collapse">
            <tbody>${tableData.join('')}</tbody>
        </table>
    `;
}

